import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import { BrowserRouter, Route, Switch, Navigate } from 'react-router-dom';
import HomePage from './Pages/HomePage';
import AboutPage from './Pages/AboutPage';
import ContactPage from './Pages/ContactPage';
import reportWebVitals from './reportWebVitals';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from './Components/Navbar';
import Footer from './Components/Footer';
import Login from '../src/Pages/LoginPage';
import AdminDashboard from './Pages/AdminDashboard';

// verifică dacă utilizatorul este autentificat
const isAuth = () => {
  // verifică dacă token-ul este salvat în localStorage și este valid
  return localStorage.getItem('token') !== null;
};

// ruta protejată pentru AdminDashboard
const ProtectedRoute = ({ children, ...rest }) => {
  // dacă utilizatorul este autentificat, permite accesul la AdminDashboard
  // altfel, redirecționează către pagina de login
  return (
    <Route
      {...rest}
      render={({ location }) =>
        isAuth() ? (
          children
        ) : (
          <Navigate to={{ pathname: "/login", state: { from: location } }} />
        )
      }
    />
  );
};

ReactDOM.render(
  <React.StrictMode>
    <BrowserRouter>
      <Navbar />
      <Routes>
        <Route exact path="/" component={HomePage} />
        <Route path="/about" component={AboutPage} />
        <Route path="/contact" component={ContactPage} />
        <Route path="/login" component={Login} />
        <ProtectedRoute path="/admin">
          <AdminDashboard />
        </ProtectedRoute>
        <Route path="*" component={() => <h1>Page Not Found</h1>} />
      </Routes>
      <Footer />
    </BrowserRouter>
  </React.StrictMode>,
  document.getElementById('root')
);

reportWebVitals();
