import { useState } from "react";
import Modal from "../Components/Modal"; // importa componenta Modal (poate fi folosita pentru a afisa coșul de cumpărături)

function Card({ title, description, price, onAddToCart }) {
  return (
    <div className="card">
      <img
        className="card-img-top"
        src="https://via.placeholder.com/350x200"
        alt="Card image cap"
      />
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">{description}</p>
      </div>
      <div className="card-footer text-center">
        <button className="btn btn-primary" onClick={onAddToCart}>
          {`Cumpara - ${price}`}
        </button>
      </div>
    </div>
  );
}

function CardGroup() {
  const cards = [
    // cardurile tale
  ];
  
  const [cart, setCart] = useState([]); // starea pentru produsele selectate
  
  const [showModal, setShowModal] = useState(false); // starea de afisare a modalului

  const handleAddToCart = (id) => {
    const selectedCard = cards.find((card) => card.id === id);
    setCart((prevCart) => [...prevCart, selectedCard]);
  }; // adauga un produs in coș

  const handleRemoveFromCart = (id) => {
    setCart((prevCart) => prevCart.filter((card) => card.id !== id));
  }; // elimina un produs din coș

  const openModal = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  const handleModalClose = () => {
    closeModal();
  };

  return (
    <div className="container">
      <div className="row row-cols-1 row-cols-md-3 g-4">
        {cards.map(({ id, title, description, price }) => (
          <div className="col" key={id}>
            <Card
              title={title}
              description={description}
              price={price}
              onAddToCart={() => handleAddToCart(id)}
            />
          </div>
        ))}
      </div>
      <Modal show={showModal} handleClose={handleModalClose}>
        <div className="card-group">
          {cart.map(({ id, title, price }) => (
            <div className="card" key={id}>
              <div className="card-body">
                <h5 className="card-title">{title}</h5>
                <p className="card-text">{price}</p>
              </div>
              <div className="card-footer">
                <button
                  className="btn btn-danger"
                  onClick={() => handleRemoveFromCart(id)}
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>
      </Modal>
    </div>
  );
}

export default CardGroup;
