function Card({ id, title, description, price, handleBuy }) {
  return (
    <div className="card">
      <img
        className="card-img-top"
        src="https://via.placeholder.com/350x200"
        alt="Card image cap"
      />
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">{description}</p>
      </div>
      <div className="card-footer text-center">
        <button className="btn btn-primary" onClick={() => handleBuy(id)}>
          {`Cumpara - ${price}`}
        </button>
      </div>
    </div>
  );
}

function CardGroup() {
  const [cart, setCart] = useState([]);
  const [selectedCard, setSelectedCard] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const handleBuy = (id) => {
    setSelectedCard(id);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleAddToCart = (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    const firstName = formData.get("firstName");
    const lastName = formData.get("lastName");
    const email = formData.get("email");
    const item = {
      id: selectedCard,
      title: cards.find((card) => card.id === selectedCard).title,
      price: cards.find((card) => card.id === selectedCard).price,
      firstName,
      lastName,
      email,
    };
    setCart([...cart, item]);
    setShowModal(false);
    setSelectedCard(null);
  };

function CardGroup() {
  const cards = [
    {
      id: 1,
      title: "Slabire rapida",
      description: "Programul perfect pentru a slabi intr-un timp record",
      price: "200 RON",
    },
    {
      id: 2,
      title: "Tone up",
      description: "Programul",
      price: "150 RON",
    },
    {
      id: 3,
      title: "Cardio intens",
      description: "Programul care iti creste rezistenta si iti imbunatateste conditia fizica",
      price: "100 RON",
    },
    {
      id: 4,
      title: "Abdomene de otel",
      description: "Programul care te ajuta sa obtii un abdomen plat si musculos",
      price: "175 RON",
    },
    {
      id: 5,
      title: "Meditatie si relaxare",
      description: "Programul pentru a-ti recupera energia si a te elibera de stres",
      price: "120 RON",
    },
    {
      id: 6,
      title: "Yoga pentru incepatori",
      description: "Programul pentru a descoperi beneficiile yoga si a-ti imbunatati flexibilitatea",
      price: "90 RON",
    },
    {
      id: 7,
      title: "Kick-boxing",
      description: "Programul care iti creste puterea, flexibilitatea si viteza",
      price: "220 RON",
    },
    {
      id: 8,
      title: "Crosfit",
      description: "Programul care iti creste masa musculara si iti imbunatateste conditia fizica generala",
      price: "250 RON",
    },
    {
      id: 9,
      title: "Antrenament functional",
      description: "Programul care iti imbunatateste mobilitatea si forta musculara",
      price: "180 RON",
    },
  ];

  return (
    <div className="container">
      <div className="row row-cols-1 row-cols-md-3 g-4">
        {cards.map(({ id, title, description, price }) => (
          <div className="col" key={id}>
            <Card title={title} description={description} price={price} />
          </div>
        ))}
      </div>
    </div>
  );
}
}
export default CardGroup;
