import React, { useState } from 'react';
import { 
  MDBBtn, 
  MDBContainer, 
  MDBRow, 
  MDBCol, 
  MDBCard, 
  MDBCardBody, 
  MDBInput, 
  MDBCheckbox 
} from 'mdb-react-ui-kit';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isChecked, setIsChecked] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();

    // Verificare câmpuri obligatorii
    if (!email || !password) {
      alert('Vă rugăm să completați toate câmpurile obligatorii.');
      return;
    }

    // Verificare format email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      alert('Vă rugăm să introduceți o adresă de email validă.');
      return;
    }

    // Verificare parolă
    if (password.length < 6) {
      alert('Vă rugăm să introduceți o parolă cu cel puțin 6 caractere.');
      return;
    }

    // Trimitere date către server sau afișare mesaj de succes
    alert('Datele au fost trimise către server.');
  };

  return (
    <MDBContainer fluid className='p-4'>
      <MDBRow>
        <MDBCol md='6' className='text-center text-md-start d-flex flex-column justify-content-center'>
          <h1 className="my-5 display-3 fw-bold ls-tight px-3">
            The best offer <br />
            <span className="text-primary">for your business</span>
          </h1>
          <p className='px-3' style={{ color: 'hsl(217, 10%, 50.8%)' }}>
            Lorem ipsum dolor sit amet consectetur adipisicing elit.
            Eveniet, itaque accusantium odio, soluta, corrupti aliquam
            quibusdam tempora at cupiditate quis eum maiores libero
            veritatis? Dicta facilis sint aliquid ipsum atque?
          </p>
        </MDBCol>

        <MDBCol md='6'>
          <MDBCard className='my-5'>
            <MDBCardBody className='p-5'>
              <form onSubmit={handleSubmit}>
                <MDBInput wrapperClass='mb-4' label='Email' id='emailInput' type='email' value={email} onChange={(e) => setEmail(e.target.value)} required />
                <MDBInput wrapperClass='mb-4' label='Password' id='passwordInput' type='password' value={password} onChange={(e) => setPassword(e.target.value)} required />
                <div className='d-flex justify-content-center mb-4'>
                  <MDBCheckbox name='newsletter' value='' id='newsletterCheckbox' label='Subscribe to our newsletter' checked={isChecked} onChange={() => setIsChecked(!isChecked)} />
                </div>
                <MDBBtn type='submit' color='primary'>Sign Up</MDBBtn>
              </form>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}

export default App;
