import React, { useState } from 'react';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBCheckbox,
  MDBIcon,
  MDBAlert
}
  from 'mdb-react-ui-kit';


function App() {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailAlert, setEmailAlert] = useState(false);
  const [passwordAlert, setPasswordAlert] = useState(false);

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
    setEmailAlert(false);
  }

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
    setPasswordAlert(false);
  }

  const handleFormSubmit = (event) => {
    event.preventDefault();
    if (email === '') {
      setEmailAlert(true);
    }
    if (password === '') {
      setPasswordAlert(true);
    }
    if (email !== '' && password !== '') {
      alert(`Email: ${email} \nPassword: ${password}`);
    }
  }

  return (
    <MDBContainer fluid className='p-4'>

      <MDBRow>

        <MDBCol md='6' className='text-center text-md-start d-flex flex-column justify-content-center'>

          <h1 className="my-5 display-3 fw-bold ls-tight px-3">
            The best offer <br />
            <span className="text-primary">for your business</span>
          </h1>

          <p className='px-3' style={{ color: 'hsl(217, 10%, 50.8%)' }}>
            Lorem ipsum dolor sit amet consectetur adipisicing elit.
            Eveniet, itaque accusantium odio, soluta, corrupti aliquam
            quibusdam tempora at cupiditate quis eum maiores libero
            veritatis? Dicta facilis sint aliquid ipsum atque?
          </p>

        </MDBCol>

        <MDBCol md='6'>

          <MDBCard className='my-5'>
            <MDBCardBody className='p-5'>

              {emailAlert &&
                <MDBAlert color='danger'>
                  Please enter your email address.
                </MDBAlert>
              }

              {passwordAlert &&
                <MDBAlert color='danger
                '>
Please enter your password.
</MDBAlert>
}
<form onSubmit={handleFormSubmit}>
            <MDBInput wrapperClass='mb-4' label='Email' id='form1' type='email' value={email} onChange={handleEmailChange} />
            <MDBInput wrapperClass='mb-4' label='Password' id='form1' type='password' value={password} onChange={handlePasswordChange} />

            <div className='d-flex justify-content-center mb-4'>
              <MDBCheckbox name='flexCheck' value='' id='flexCheckDefault' label='Subscribe to our newsletter' />
            </div>

            <MDBBtn type='submit' color='primary'>Sign Up</MDBBtn>

          </form>

        </MDBCardBody>
      </MDBCard>

    </MDBCol>

  </MDBRow>

</MDBContainer>
);
}

export default App;
