import React from "react";
import '../styles/cards.css';
import  { useState } from "react";



function Card({ title, description, price, onAddToCart }) {
  return (
    <div className="card">
      <img
        className="card-img-top"
        src="https://via.placeholder.com/350x200"
        alt="Card image cap"
      />
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">{description}</p>
      </div>
      <div className="card-footer text-center">
        <button className="btn btn-primary" onClick={onAddToCart}>{`Cumpara - ${price}`}</button>
      </div>
    </div>
  );
}
function CartModal({ show, handleClose, items }) {
  const totalPrice = items.reduce((acc, item) => acc + item.price, 0);

  return (
    <div className={show ? "modal display-block" : "modal display-none"}>
      <section className="modal-main">
        <div className="modal-header">
          <h5 className="modal-title">Coș de cumpărături</h5>
          <button type="button" className="close" onClick={handleClose}>
            <span>&times;</span>
          </button>
        </div>
        <div className="modal-body">
          <ul>
            {items.map((item) => (
              <li key={item.id}>
                {item.title} - {item.price}
              </li>
            ))}
          </ul>
          <p>Total: {totalPrice} RON</p>
        </div>
        <div className="modal-footer">
          <button type="button" className="btn btn-secondary" onClick={handleClose}>
            Închide
          </button>
          <button type="button" className="btn btn-primary">
            Finalizează comanda
          </button>
        </div>
      </section>
    </div>
  );
}
function ItemCard({ item, handleAddToCart }) {
  return (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">{item.title}</h5>
        <p className="card-text">{item.description}</p>
        <p className="card-text">Preț: {item.price} RON</p>
        <button className="btn btn-primary" onClick={() => handleAddToCart(item)}>
          Adaugă în coș
        </button>
      </div>
    </div>
  );
}

function TrainingPage() {
  const [cartItems, setCartItems] = useState([]);
  const [showCartModal, setShowCartModal] = useState(false);

  const handleAddToCart = (item) => {
    setCartItems((prevItems) => [...prevItems, item]);
  };

  const handleShowCartModal = () => {
    setShowCartModal(true);
  };

  const handleCloseCartModal = () => {
    setShowCartModal(false);
  };

  return (
    <div>
      <div className="items">
        {items.map((item) => (
          <ItemCard key={item.id} item={item} handleAddToCart={handleAddToCart} />
        ))}
      </div>
      <CartModal show={showCartModal} handleClose={handleCloseCartModal} items={cartItems} />
      <button className="btn btn-primary" onClick={handleShowCartModal}>
        Vezi coșul de cumpărături
      </button>
    </div>
  );
}









function Modal({ show, handleClose, card }) {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");

  const handleFirstNameChange = (e) => {
    setFirstName(e.target.value);
  };

  const handleLastNameChange = (e) => {
    setLastName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log(`Comanda: ${card.title} - ${firstName} ${lastName} - ${email}`);
    handleClose();
  };

  return (
    <div className={show ? "modal display-block" : "modal display-none"}>
      <section className="modal-main">
        <div className="modal-header">
          <h5 className="modal-title">Comanda: {card.title}</h5>
          <button type="button" className="close" onClick={handleClose}>
            <span>&times;</span>
          </button>
        </div>
        <div className="modal-body">
          <div className="selected-card">
            <img
              className="selected-card-img"
              src="https://via.placeholder.com/150x100"
              alt="Selected card"
            />
            <div className="selected-card-info">
              <h5>{card.title}</h5>
              <p>{card.description}</p>
            </div>
          </div>
          <div className="form-group">
            <label htmlFor="firstName">First Name:</label>
            <input
              type="text"
              className="form-control"
              id="firstName"
              placeholder="Enter first name"
              value={firstName}
              onChange={handleFirstNameChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="lastName">Last Name:</label>
            <input
              type="text"
              className="form-control"
              id="lastName"
              placeholder="Enter last name"
              value={lastName}
              onChange={handleLastNameChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              className="form-control"
              id="email"
              placeholder="Enter email"
              value={email}
              onChange={handleEmailChange}
            />
          </div>
        </div>
        <div className="modal-footer">
          <button type="button" className="btn btn-secondary" onClick={handleClose}>
            Close
          </button>
          <button type="submit" className="btn btn-primary" onClick={handleFormSubmit}>
            Submit
          </button>
        </div>
      </section>
    </div>
  );
}






function CardGroup() {
  const citems = [
    {
      id: 1,
      title: "Slabire rapida",
      description: "Programul perfect pentru a slabi intr-un timp record",
      price: "200 RON",
    },
    {
      id: 2,
      title: "Tone up",
      description: "Programul",
      price: "150 RON",
